INSERT INTO `wp_comments` VALUES("1", "1", "Um comentarista do WordPress", "wapuu@wordpress.example", "https://wordpress.org/", "", "2020-12-03 01:21:30", "2020-12-03 04:21:30", "Olá, isso é um comentário.\nPara começar a moderar, editar e excluir comentários, visite a tela de Comentários no painel.\nAvatares de comentaristas vêm a partir do <a href=\"https://gravatar.com\">Gravatar</a>.", "0", "1", "", "comment", "0", "0");
